<?phprequire_once('config.php');require_once('abby.php');include 'sendgrid/sendgrid-php/SendGrid_loader.php';//Get Post from SendGrid                                                                        $from = $_POST["from"];$subject = $_POST["subject"];//Get Attached Image                                                                            $fh = fopen('/tmp/parse.log', 'a+');if ( $fh )  {    fwrite($fh, print_r($_POST, true) . print_r($_FILES, true));    foreach ($_FILES as $key => $file)      {        move_uploaded_file($file['tmp_name'], "upload/".$file['name']);      }    fclose($fh);  }//Convert Image to Text                                                                         $ocrapi = new Abby($config['abby']['app_id'], $config['abby']['password']);$docName = $ocrapi->convert($file['name']);//Send reply email with attached text document                                                  $filePath = dirname(__FILE__).'/upload/'.$docName;$sendgrid = new SendGrid($config['sendgrid']['api_user'], $config['sendgrid']['api_key']);			     $mail = new SendGrid\Mail();			     			     			     $mail->			     addTo($from)->			     setFrom($config['sendgrid']['my_email'])->			     setSubject('Re: '.$subject)->			     setText($docName)->			     setHtml($docName)->			     addAttachment($filePath);			     $sendgrid->smtp->send($mail);			     ?>